mapper.select:

select board_subject, board_content, board_file, to_char(board_date, 'YYYY-MM-DD') as board_date
from board_table
where board_index = #{board_index}

mapper.update:

update board_table 
set board_subject = #{board_subject}, board_content = #{board_content}, board_file = #{board_file}
where board_index = #{board_index}
